import React, { Component } from 'react'
import './Menu.css'
import student from './MenuItems/StudentMenuItems'

interface Props {
    page?: string,
    changePage(event : React.MouseEvent<HTMLButtonElement>): void
}

interface State {
    items: {title: string, disabled: boolean }[]
}

class Menu extends Component<Props, State>{
    constructor(props: Props) {
        super(props);
        this.state = {
            items : student
        }
    }

    isSelected(item: boolean) {
        return item ? "selected" : ""
    }

    handleChange = (event : React.MouseEvent<HTMLButtonElement>) => {
        const page = event.currentTarget.textContent
        let arr = this.state.items.map(item => {
            if(item.title === page) {
                item.disabled = true
            }else item.disabled = false
            return item
        })
        this.setState({items:arr})
        this.props.changePage(event)
    }

    private renderMenu() {
        return (
            <div className='menu'>
                {this.state.items.map(item => {
                    return (
                        <button 
                            key = {item.title}
                            className={'menuItem ' + this.isSelected(item.disabled)}
                            onClick={this.handleChange}
                       >{item.title}
                        </button>
                    )
                })}
            </div>
        )
    };

    render() {
        return this.renderMenu();
    }
}

export default Menu
