import React from 'react'
import Select from '@skbkontur/react-ui/Select'

interface Props{

}

interface State{
    whichTopics?: string
}

class CuratorSuggestedTopics extends React.Component<Props,State>{
    constructor(props : Props){
        super(props)
        this.state = {
            whichTopics : 'Занятые темы'
        }
    }

    private handleSelectChange = (e : {target: {value: string}}) => {
        return (this.setState({whichTopics : e.target.value}))
    }

    private renderSelect(){
        return(
            <Select
                items={['Занятые темы', 'Свободные темы']}
                value={this.state.whichTopics}
                onChange={item => this.handleSelectChange(item)}
            />

        )
    }

    render(){
        return(
            <div>
                <p>{this.state.whichTopics}</p>
                {this.renderSelect()}
            </div>
        )
    }
}

export default CuratorSuggestedTopics
