import React, { Component } from 'react'
import './CourseWork.css'

interface Idata{
    title?: string,
    teacher?: string,
    deadline?: string,
    scienceArea?: string,
    description?: string,
    reportFile?: string,
    presentationFile?: string,
    consultantReportFile?: string,
    link?: string,
    teacherContacts?: string,
    consultant?: string,
    consultantContacts?: string,
    critic?: string,
    status?: string
}

interface Props{
    changePage(event : React.MouseEvent<HTMLButtonElement>) : void,
    data : Idata[]
}

interface State{

}

class CourseWork extends Component<Props,State>{
    constructor(props : Props){
        super(props);
        this.state={}
    }

    private renderCourseWork(item : Idata){
        return(
            <div className='courseWork'>
                <p className='courseWorkTitle'><b>{item.title}</b>  {', ' + item.teacher + ', до    ' + item.deadline}</p>
                <p className='courseWorkDescription'>{item.description}</p>
                <button value='Моя курсовая детально' onClick={this.props.changePage} className='courseWorkMore'>Подробнее</button>
            </div>
        )
    }

    private renderContentBar(){
        return this.props.data.map( (item: Idata) => this.renderCourseWork(item))
    }

    render(){
        return this.renderContentBar();
    }
}

export default CourseWork
