import React, {Component} from "react";
import ServiceTopBar from "./Components/TopBar/ServiceTopBar";
import Menu from './Components/Menu/Menu'
import MainWindow from './Components/MainWindow/MainWindow'

interface Props{

}

interface State{
    page: string | null
}

class App extends Component<Props,State> {
    constructor(props : Props){
        super(props);
        this.state = {
            page : "Главная"
        }
    }

    changePage = (newPage:string | null) => {
       return(this.setState({page:newPage}))
    }

    render() {
        return (
        <div className='page'>
            <ServiceTopBar/>
            <Menu page={this.state.page} changePage={this.changePage}/>
            <MainWindow page={this.state.page}/>
        <p>{this.state.page}</p>
        </div>
        )}
}

export default App;
