import React, { Component } from 'react'
import './Main.css'
import Notifications from './Components/Notifications/Notifications'
import AddWork from './Components/SidePage/SidePage'
import Button from '@skbkontur/react-ui/Button'
import Gapped from '@skbkontur/react-ui/Gapped'
import {Add, Briefcase, Delete} from '@skbkontur/react-icons'

//student
import biddingData from '../../../../../TestData/Student/biddingData'

//teacher
import teacherBiddingData from '../../../../../TestData/Teacher/biddingData'

interface Idata{
    title?: string,
    id?: number
}

interface Props{
    isCritic?: boolean,
    handleCritic() : void,
    changePage(event : React.MouseEvent<HTMLButtonElement>): void,
    role?: string,
    userId?: number,
}

interface State{
    opened?: boolean,
    biddingData?: Idata[],
    isLoading?: boolean,
}

class Main extends Component<Props,State>{
    constructor(props : Props){
        super(props);
        this.state={
            //isCritic : this.props.isCritic,
            //Надо так
            //isCritic : false,
            opened : false,
            biddingData : [{}],
            isLoading : false,
        }
    }

    componentDidUpdate(prevProps : Props){
        if(prevProps.isCritic !== this.props.isCritic)
            this.componentDidMount()
    }

    componentDidMount(){
        this.setState({isLoading : true})

        this.whichData()
        this.setState({isLoading : false})
    }



    private whichData(){
        switch(this.props.role){
            case 'student':{
                //---------------------------------------------
                //запрос по userId о данных результата биддинга (и рецензент ли пользователь)
                //---------------------------------------------

                return this.setState({biddingData : biddingData/*, isCritic : false*/})
            }
            case 'teacher':{
                //---------------------------------------------
                //запрос по userId о данных результата биддинга (и рецензент ли пользователь)
                //---------------------------------------------

                return this.setState({biddingData : teacherBiddingData/*, isCritic : false*/})
            }
        }
    }

    private changeCriticStatus = () => {
        // let newCriticStatus = !this.state.isCritic
        // this.setState({isCritic : newCriticStatus})
        this.props.handleCritic()
    }

        
    private openSidePage = () => {
      this.setState({ opened: true });
      }
    
    private closeSidePage = () => {
    this.setState({ opened: false });
    }

    private needSidePage(){
        return (this.props.role === 'teacher' || this.props.role === 'curator')?
            <div>
                {this.state.opened?
                <AddWork
                    closeSidePage={this.closeSidePage}
                />
                :null}
                <Button
                    onClick={this.openSidePage}
                    icon={<Add/>}
                >Предложить тему курсовой работы</Button>
            </div>
        : null
            
    }

    private criticButton(){
        return !this.props.isCritic?
            <Button
                icon={<Briefcase/>}
                onClick={this.changeCriticStatus}
                use='success'
            >Стать рецензентом</Button>
        : 
            <Button
                icon={<Delete/>}
                onClick={this.changeCriticStatus}
                use='danger'
            >Перестать быть рецензентом</Button>
    }

    private renderMain(){
        return(
            <div>
                {this.props.isCritic? 
                    <Notifications 
                        changePage={this.props.changePage} 
                        role={this.props.role} 
                        data={this.state.biddingData!}/> 
                : null}

                <div className='ml20'>
                    <Gapped>
                        {this.props.role !== 'curator'? this.criticButton() : null}
                        {this.needSidePage()} 
                    </Gapped>
                </div>
            </div>
        )
    }

    render(){
        return this.renderMain();
    }
}

export default Main
