import React, { Component } from 'react'
import './Content.css'
import ContentBar from './ContentBar/ContentBar'
import MyCourseWork from './MyCourseWork/MyCourseWork'
import MyCourseWorkDetail from './MyCourseWorkDetail/MyCourseWorkDetail'

interface Props{
    page?: string,
    section?: string,
    changeSection(event : React.MouseEvent<HTMLButtonElement>) : void
}

interface State{

}

class Content extends Component<Props,State>{
    constructor(props : Props){
        super(props);
        this.state ={
        }
    }

    private renderContent(){
        return(
            <div className='content'>
                {(this.props.page === 'Мои курсовые' && (this.props.section === ('Активные' || 'Завершенные' || 'Мои заявки')))? <ContentBar changeSection={this.changeSection}/> : null}
                {(this.props.page === 'Мои курсовые' && this.props.section === 'Активные')? <MyCourseWork changeSection={this.props.changeSection}/> : null}
                {(this.props.page === 'Мои курсовые' && this.props.section === 'Моя курсовая')? <MyCourseWorkDetail/> : null}
            </div>
        )
    }

    render(){
        return this.renderContent();
    }
}

export default Content
