import React from 'react'
import Link from '@skbkontur/react-ui/Link'
import Toast from '@skbkontur/react-ui/Toast'
import 'typeface-roboto';

interface Props{
    data : {
        title?: string,
        teacher?: string,
        deadline?: string,
        scienceArea?: string,
        description?: string,
        reportFile?: string,
        presentationFile?: string,
        consultantReportFile?: string,
        link?: string,
    }
}

class AttachedFiles extends React.Component<Props>{

    private downloadFile = () => {
        Toast.push('Скачивание...')
    }

    private openLink = () => {
        Toast.push('Открываю ссылку...')
    }

    styles={
        minWidth : '30px'
    }

    render(){
        return(
        <div className='attached'>
            {this.props.data.reportFile !== ''? <div><span style={this.styles}>Отчет: </span><Link use='success'onClick={this.downloadFile}>{this.props.data.reportFile}</Link></div> : null }
            {this.props.data.presentationFile !== ''? <div><span className='mw30'>Презентация: </span> <Link use='success'onClick={this.downloadFile}>{this.props.data.presentationFile}</Link></div> : null}
            {this.props.data.consultantReportFile !== ''? <div><span className='mw30'>Отзыв консультанта: </span><Link use='success'onClick={this.downloadFile}>{this.props.data.consultantReportFile}</Link></div> : null}
            {this.props.data.link !== ''? <div><span className='mw30'>Ссылка: </span><Link use='success'onClick={this.openLink}>{this.props.data.link}</Link></div> : null}
            
        </div>
        )
    }
}

export default AttachedFiles
