import React, { Component } from 'react'
import './ContentBar.css'
import  Gapped  from '@skbkontur/react-ui/Gapped';
import Button from '@skbkontur/react-ui/Button'
import ThemeContext from '@skbkontur/react-ui/lib/theming/ThemeContext';
import ThemeFactory from '@skbkontur/react-ui/lib/theming/ThemeFactory'

interface Props{

}

interface State{

}

class ContentBar extends Component<Props,State>{
    constructor(props : Props){
        super(props);
        this.state={}
    }

    buttonTheme = ThemeFactory.create({ border: 'black 1px solid' });

    private renderContentBar(){
        return(
            <div className='contentBar'>
                <Gapped>
                    <ThemeContext.Provider value={buttonTheme}>
                        <Button use="default" checked={true}>Default</Button>
                    </ThemeContext.Provider>
                        <Button use="default">Default</Button>
                        <Button use="default">Default</Button>

                </Gapped>
            </div>
        )
    }

    render(){
        return this.renderContentBar();
    }
}

export default ContentBar
