import React, { Component } from 'react'
import data from '../MyCourseWorkData'
import './MyCourseWorkDetail.css'
import Input from '@skbkontur/react-ui/Input'
import Button from '@skbkontur/react-ui/Button'
import Gapped from '@skbkontur/react-ui/Gapped'
import Link from "@skbkontur/react-ui/Link"
import Toast from '@skbkontur/react-ui/Toast'

interface Props{

}

interface State{

}

class MyCourseWork extends Component<Props,State>{
    constructor(props : Props){
        super(props);
        this.state={}
    }

    private attachLink = () => {
        Toast.push("Ссылка прикреплена");
    }

    private deleteLink = () => {
        Toast.push("Ссылка удалена")
    }

    private renderContentBar(){
        return(
            <div className='informationWindow'>
                <p className='descriptionDetail'>{data.description}</p>
                <div className='attached'>
                    <p>Прикрепленные файлы: </p><p>{<Link>{data.file}</Link>}</p>
                    <p>Прикрепленные ссылки: {<Link>{data.link}</Link>}</p>
                </div>
                <div className='gapped'>
                    <Gapped>
                        <p>Прикрепить</p>
                        <Button size='medium' use='success'>Презентацию</Button>
                        <Button size='medium' use='success'>Отзыв консультанта</Button>
                        <Button size='medium' use='success'>Отчет</Button>
                        <p>Удалить</p>
                        <Button size='medium' use='danger'>Отчет</Button>
                        <Button size='medium' use='danger'>Презентацию</Button>
                    </Gapped>
                </div>
                <div className='inputLink'>
                    <Gapped>
                        <Input prefix='https://' width={400}/>
                        <Button size='small' onClick={this.attachLink}>Прикрепить</Button>
                        <Link use='grayed' onClick={this.deleteLink}>Удалить ссылку</Link>
                    </Gapped>
                </div>
                <hr/>
            </div>
        )
    }

    render(){
        return this.renderContentBar();
    }
}


export default MyCourseWork
