import React, { Component } from 'react'
import './Content.css'
import ContentBar from './ContentBar/ContentBar'

interface Props{
    page?: string
}

interface State{
    section?: string
}

class Content extends Component<Props,State>{
    constructor(props : Props){
        super(props);
        this.state ={
            section:'Активные'
        }
    }

    changeSection(event: React.MouseEvent<HTMLButtonElement>){
        const newSection = event.currentTarget.textContent
        this.setState({section : newSection!})
    }

    private renderContent(){
        return(
            <div className='content'>
                {this.props.page==='Мои курсовые'? <ContentBar changeSection={this.changeSection}/> : null}
                <p>{this.props.page}</p>
                {this.props.page === 'Мои курсовые'? <p>{this.state.section}</p> : null}
            </div>
        )
    }

    render(){
        return this.renderContent();
    }
}

export default Content
