import React, { Component } from 'react'
import './CourseWork.css'
import Typography from '@material-ui/core/Typography'

interface Idata{
    title?: string,
    teacher?: string,
    deadline ?: string,
    scienceArea?: string,
    description?: string,
    reportFile?: string,
    presentationFile?: string,
    consultantReportFile?: string,
    link?: string,
    teacherContacts?: string,
    consultant?: string,
    consultantContacts?: string,
    critic?: string,
    status?: string,
    id?: number
}

interface Props{
    changePage(event : React.MouseEvent<HTMLButtonElement>) : void,
    data : Idata,
}

interface State{

}

class CourseWork extends Component<Props,State>{
    constructor(props : Props){
        super(props);
        this.state={}
    }

    private isEmpty(obj : Idata) {
        return Object.keys(obj).length === 0;
    }

    private renderCourseWork(){
        return (
            !this.isEmpty(this.props.data)?
            <div className='courseWork'>
                <p className='courseWorkTitle'><b>{this.props.data.title}</b>  {', ' + this.props.data.teacher + ', до    ' + this.props.data.deadline}</p>
                <p className='courseWorkDescription'>{this.props.data.description}</p>
                <button 
                    value={this.props.data.id === 0? 'Моя курсовая детально' : this.props.data.id!.toString()} 
                    onClick={this.props.changePage} 
                    className='courseWorkMore'
                >Подробнее</button>
            </div>
            :             
            <div style={{textAlign:"center"}}>
                <Typography variant='h5'>Нет завершенных курсовых работ</Typography>
            </div>
        )
    }


    render(){
        return this.renderCourseWork();
    }
}

export default CourseWork
