import React, { Component } from 'react'
import './Menu.css'
import student from './MenuItems/StudentMenuItems'

interface Props {
    page: string 
}

interface State {
    page:string,
    items: {title: string, disabled: boolean }[]
}

class Menu extends Component<Props, State>{
    constructor(props: Props) {
        super(props);
        this.state = {
            page : props.page,
            items : student
        }
    }

    isSelected(item: boolean) {
        return item ? "selected" : ""
    }

    handleChange = (event : React.MouseEvent<HTMLDivElement>) => {
        let arr = this.state.items.map(item => {
            if(item.title === event.currentTarget.textContent) {
                item.disabled = true

            }else item.disabled = false
            return item
        })
        this.setState({items:arr, page:event.currentTarget.textContent})

    }

    private main() {
        return (
            <div className='menu'>
                {this.state.items.map(item => {
                    return (
                        <div 
                            className={'menuItem ' + this.isSelected(item.disabled)}
                            onClick={this.handleChange}

                        >{item.title}
                        </div>
                    )
                })}
            </div>
        )
    };

    render() {
        return this.main();
    }
}

export default Menu
