import React from 'react'
import Login from './Login'
import Register from './Register'
import Typography from '@material-ui/core/Typography'
import Switcher from '@skbkontur/react-ui/Switcher' 
import './AuthPage.css'

interface Props{
    auth () : void
}

interface State{
    switcher?: string
}

export default class AuthPage extends React.Component<Props, State>{
    constructor(props : Props){
        super(props)
        this.state = {
            switcher: 'login'
        }
    }

    render(){
        return(
            <div>
                <div className='authTitle'>
                    <Typography variant='h4'>HwProj Course Work - сервис курсовых работ</Typography>
                </div>
                <div>
                    <Switcher
                        value={this.state.switcher}
                        items={['Войти', 'Зарегистрироваться']}
                        onChange={(e: {target: {value:string}}) => {this.setState({switcher : e.target.value})}}
                    />
                    {this.state === 'login'?
                        <Login auth={this.props.auth}/>
                    :   <Register auth={this.props.auth}/>}
                </div>

            </div>
    )}
}
