import React, { Component } from 'react'

interface Props {

}

interface State {
    items: { title: string, disabled: boolean }[]
}

class Menu extends Component<Props, State>{
    constructor(props: Props) {
        super(props);
        this.state = {
            items: [
                {
                    title: 'Главная',
                    disabled: true
                },
                {
                    title: 'Мои курсовые',
                    disabled: false
                },
                {
                    title: 'Свободные курсовые',
                    disabled: false
                }
            ]
        }
    }

    style(item: boolean) {
        return item ? "menuItem-selected" : "menuItem"
    }

    private main() {
        return (
            <div className='menu'>
                {this.state.items.map(item => {
                    return (
                        <div className={this.style(item.disabled)}>
                            {item.title}
                        </div>
                    )
                })}
            </div>
        )
    };

    render() {
        return this.main();
    }
}

export default Menu
