import React, { Component, RefObject } from 'react'
import courseWorkData from '../MyCourseWorkData'
import './MyCourseWorkDetail.css'
import Input from '@skbkontur/react-ui/Input'
import Button from '@skbkontur/react-ui/Button'
import Gapped from '@skbkontur/react-ui/Gapped'
import Link from "@skbkontur/react-ui/Link"
import Toast from '@skbkontur/react-ui/Toast'
import Spinner from '@skbkontur/react-ui/Spinner'
import Center from '@skbkontur/react-ui/Center'
import Select from '@skbkontur/react-ui/Select'
import AttachedFiles from './Components/AttachedFiles'

interface Props{

}

interface State{
    isLoading: boolean,
    data : {
        title?: string,
        teacher?: string,
        deadline?: string,
        scienceArea?: string,
        description?: string,
        reportFile?: string,
        presentationFile?: string,
        consultantReportFile?: string,
        link?: string,
    },
    newLink?: string,
    attachSelect : {target?:{value?:string}},
    deleteSelect : {target?: {value?: string}}
}

class MyCourseWork extends Component<Props,State>{
    private readonly inputOpenFileRef : RefObject<HTMLInputElement>

    constructor(props : Props){
        super(props);
        this.state={
            isLoading : false,
            data:courseWorkData,
            newLink : '',
            attachSelect : {target : {value : 'Выбрать'}},
            deleteSelect : {target : {value : 'Выбрать'}}
        }
        this.inputOpenFileRef = React.createRef()
    }

    private attachLink = () => {
        if(this.state.newLink === 'http://')
            Toast.push('Введите ссылку')
        else{
        const arr = this.state.data;
        arr.link = this.state.newLink;
        this.setState({data:arr})
        Toast.push('Ссылка ' + this.state.data.link + ' прикреплена');
        }
    }

    private deleteLink = () => {
        if(this.state.data.link === '')
            Toast.push('Ссылки нет')
        else{
        const arr = this.state.data;
        arr.link =''
        this.setState({data:arr})
        Toast.push("Ссылка удалена")
        }
    }


    handleNewLink = (event : React.ChangeEvent<HTMLInputElement>, value:string) => {
        this.setState({newLink:'http://' + value})
    }

    downloadFile = (event: React.MouseEvent<HTMLAnchorElement>) => {
        Toast.push('Скачивание файла: '+ event.currentTarget.textContent)
    }

    deleteFile = () => {
        const arr =this.state.data;
        const target = this.state.deleteSelect.target!.value;
        switch(target){
            case 'Отчет':{
                if(arr.reportFile === '') Toast.push('Отчета нет')
                arr.reportFile ='';
                break;
            }
            case 'Презентацию':{
                if(arr.presentationFile === '') Toast.push('Презентации нет')
                arr.presentationFile= '';
                break;
            }
            case 'Отзыв консультанта':{
                if(arr.consultantReportFile === '') Toast.push('Отзыва консультанта нет')
                arr.consultantReportFile= '';
                break;
            }
            default:{
                Toast.push('Выберите, что удалить')
                console.log('Nothing to delete');
            }
        }
        this.setState({data:arr})
}

    componentDidMount(){
        this.setState({isLoading:true})
        setTimeout(() => {
            this.setState({
                isLoading: false
            })
        }, 1500)
    }

    attachFile = (fileList: FileList) => {
        const file = fileList[0].name;
        const arr = this.state.data;
        const target = this.state.attachSelect.target!.value;
        if(file === this.state.data.reportFile || file === this.state.data.presentationFile || file === this.state.data.consultantReportFile) 
            Toast.push('Прикрепляйте разные файлы')
        switch(target){
            case 'Отчет': {
                arr.reportFile = file;
                break;
            };
            case 'Презентацию':{
                arr.presentationFile = file;
                break;
            };
            case 'Отзыв консультанта':{
                arr.consultantReportFile = file;
                break
            };
            default:{
                Toast.push('Выберите, что прикрепить')
                console.log('Nothing to attach');
            }
        }
        // eslint-disable-next-line
        this.setState({data:arr})
    };


    private renderContentBar(){
        let attachItems = ['Выбрать',Select.SEP,'Отчет', 'Презентацию', 'Отзыв консультанта'];
        let deleteItems = ['Выбрать',Select.SEP, 'Отчет', 'Презентацию', 'Отзыв консультанта'];
        return(
            <div className='informationWindow'>
                {!this.state.isLoading? 
                    <div>
                        <p className='descriptionDetail'>{this.state.data.description}</p>
                        <AttachedFiles data = {this.state.data}/>
                        <div className='gapped'>
                            <Gapped gap={20}>
                                <div className='attachFiles'>
                                    <p>Прикрепить:</p>
                                    <Select
                                        width='200px' 
                                        placeholder='Выбрать'
                                        items={attachItems}
                                        onChange={(attachSelect:{}) => this.setState({ attachSelect })} 
                                    />
                                    {this.state.attachSelect.target!.value !== 'Выбрать'?
                                    <input 
                                        className='inputAttach' 
                                        name={this.state.attachSelect.target!.value} 
                                        id = {this.state.attachSelect.target!.value}
                                        type="file" 
                                        onChange={(e) => this.attachFile(e.target.files!)}
                                    />
                                    : null}
                                </div>
                                <div className='deleteFiles'>
                                    <p>Удалить:</p>
                                    <Select
                                        width='200px'
                                        placeholder='Выбрать'
                                        items = {deleteItems}
                                        onChange={(deleteSelect:{}) => this.setState({ deleteSelect })}
                                    />
                                </div>
                                {this.state.deleteSelect.target!.value !== 'Выбрать'?
                                <Button size='small' use='link' onClick={this.deleteFile}>Удалить</Button>
                                : null}

                            </Gapped>
                        </div>
                        <div className='inputLink'>
                            <Gapped>
                                <Input width={400} name='attachLink' onChange={this.handleNewLink} prefix='http://'/>
                                <Button size='small' onClick={this.attachLink}>Прикрепить</Button>
                                <Link use='grayed'onClick={this.deleteLink}>Удалить ссылку</Link>
                            </Gapped>
                        </div>
                        <hr/>
                    </div> : <Center><Spinner type='big' caption='Загрузка'/></Center>}
            </div>
        )
    }

    render(){
        return this.renderContentBar();
    }
}


export default MyCourseWork
