import React, { Component } from 'react'
import './Content.css'
import ContentBar from './ContentBar/ContentBar'
import CourseWork from './CourseWork/CourseWork'
import MyCourseWorkDetail from './MyCourseWorkDetail/MyCourseWorkDetail'
import CompletedWorksList from './CompletedWorksList/CompletedWorksList'
import CourseWorkDetail from './CourseWorkDetail/CourseWorkDetail'
import RequestsList from './RequestsList/RequestsList'

import activeData from '../../../../TestData/activeWorkData'
import completedData from '../../../../TestData/completedWorksData'
import requestsData from '../../../../TestData/requestsData'


interface Props{
    page?: string,
    changePage(event : React.MouseEvent<HTMLButtonElement>) : void
}

interface State{

}

class Content extends Component<Props,State>{
    constructor(props : Props){
        super(props);
        this.state ={
        }
    }

    needContentBar(page? : string){
        if(page === 'Активные' || page === 'Завершенные' || page === 'Мои заявки')
            return true
        else return false
    }

    private whichContent(){
        if(Number(this.props.page)) 
            return <CourseWorkDetail data={completedData[Number(this.props.page)-1]}/>
        switch(this.props.page){
            case 'Активные': return <CourseWork data={activeData[0]} changePage={this.props.changePage}/>
            case 'Моя курсовая детально': return <MyCourseWorkDetail/>
            case 'Завершенные': return <CompletedWorksList data={completedData} changePage={this.props.changePage}/>
            case 'Мои заявки': return <RequestsList data={requestsData} changePage={this.props.changePage}/>
        }
    }

    private renderContent(){
        return(
            <div className='content'>
                {this.needContentBar(this.props.page)? <ContentBar changePage={this.props.changePage} page={this.props.page}/> : null}
                {this.whichContent()}
            </div>
        )
    }

    render(){
        return this.renderContent();
    }
}

export default Content
