import React, { Component } from 'react'
import './ContentBar.css'
import contentBarData from './ContentBarData';


interface Props{
    section?: string,
    changeSection(event: React.MouseEvent<HTMLButtonElement>): void;
}

interface State{
    items: {section?:string, selected?: boolean}[]
}

class ContentBar extends Component<Props,State>{
    constructor(props : Props){
        super(props);
        this.state={
            items: contentBarData
        }
    }

    isSelected(item?:boolean){
        return item? 'btnSelected' : ''
    }

    private renderContentBar(){
        return(
            <div className='contentBar'>
                {this.state.items.map(item => 
                    <button 
                        onClick={this.props.changeSection} 
                        className={'button '+ this.isSelected(item.selected)}
                        value={item.section}
                    >{item.section}
                    </button>)}
                <hr className='minor'/>
            </div>
        )
    }

    render(){
        return this.renderContentBar();
    }
}

export default ContentBar
