import React, { Component } from 'react'
import './Content.css'
import ContentBar from './ContentBar/ContentBar'
import CourseWork from './CourseWork/CourseWork'
import MyCourseWorkDetail from './MyCourseWorkDetail/MyCourseWorkDetail'
import CompletedWorks from './CompletedWorks/CompletedWorks'
import activeData from './activeWorkData'
import completedData from './completedWorksData'

interface Props{
    page?: string,
    changePage(event : React.MouseEvent<HTMLButtonElement>) : void
}

interface State{

}

class Content extends Component<Props,State>{
    constructor(props : Props){
        super(props);
        this.state ={
        }
    }

    needContentBar(page? : string){
        if(page === 'Активные' || page === 'Завершенные' || page === 'Мои заявки')
            return true
        else return false
    }

    private whichContent(){
        switch(this.props.page){
            case 'Активные': return <CourseWork data={activeData[0]} changePage={this.props.changePage}/>
            case 'Моя курсовая детально': return <MyCourseWorkDetail/>
            case 'Завершенные': return <CompletedWorks data={completedData} changePage={this.props.changePage}/>
        }
    }

    private renderContent(){
        return(
            <div className='content'>
                {this.needContentBar(this.props.page)? <ContentBar changePage={this.props.changePage} page={this.props.page}/> : null}
                {this.whichContent()}
            </div>
        )
    }

    render(){
        return this.renderContent();
    }
}

export default Content
