import React, { Component } from 'react'
import './Content.css'
import ContentBar from './ContentBar/ContentBar'
import CourseWork from './CourseWork/CourseWork'
import MyCourseWorkDetail from './MyCourseWorkDetail/MyCourseWorkDetail'
import WorksList from './WorksList/WorksList'
import CourseWorkDetail from './CourseWorkDetail/CourseWorkDetail'
import RequestsList from './RequestsList/RequestsList'
import RequestDetail from './RequestDetail/RequestDetail'
import FreeWorkDetail from './FreeWorkDetail/FreeWorkDetail'
import Main from './Main/Main'
import RequireCriticList from './RequireCriticList/RequireCriticList'
import RequireCriticDetail from './RequireCriticDetail/RequireCriticDetail'
import BiddingDetail from './BiddingDetail/BiddingDetail'
import TeachersCurrentWork from './TeachersCurrentWork/TeachersCurrentWork'
import TeacherFreeWorksDetail from './TeacherFreeWorkDetail/TeacherFreeWorkDetail'
import CriticSwitcher from './CriticSwitcher/CriticSwitcher'
import NewCriticList from './NewCriticsList/NewCriticList'

//student
import activeWork from '../../../../TestData/Student/activeWorkData'
import completedWorks from '../../../../TestData/Student/completedWorksData'
import requestsData from '../../../../TestData/Student/requestsData'
import freeWorks from '../../../../TestData/Student/freeWorksData'
import requireData from '../../../../TestData/Student/requireCriticData'
import biddingData from '../../../../TestData/Student/biddingData'

//teacher
import teacherCurrentWorks from '../../../../TestData/Teacher/currentWorks'
import teacherMyFreeWorks from '../../../../TestData/Teacher/myFreeWorks'
import teacherRequest from '../../../../TestData/Teacher/requestsData'
import teacherCompletedWorks from '../../../../TestData/Teacher/completedWorks'
import teacherFreeWorks from '../../../../TestData/Teacher/freeWorks'
import teacherRequireCritic from '../../../../TestData/Teacher/requireCriticData'
import teacherBidding from '../../../../TestData/Teacher/biddingData'

//curator


interface IRequestsData{
    title?: string,
    teacher?: string,
    scienceArea?: string,
    description?: string,
    teacherContacts?: string,
    aboutMe?: string,
    id?: number
}

interface ICompletedData{
    title?: string,
    teacher?: string,
    deadline?: string,
    scienceArea?: string,
    description?: string,
    reportFile?: string,
    presentationFile?: string,
    consultantReportFile?: string,
    link?: string,
    teacherContacts?: string,
    consultant?: string,
    consultantContacts?: string,
    critic?: string,
    status?: string,
    teacherReview?: string,
    criticReview?: string,
    id?: number
}

interface IFreeData{
    title?: string,
    teacher?:string,
    teacherContacts?: string,
    scienceArea?: string,
    description?: string, 
    deadline?: string,
    id?: number
}

interface IRequireCriticData{
    title?: string,
    teacher?: string,
    teacherContacts?: string,
    scienceArea?: string,
    description?: string, 
    reportFile?: string,
    presentationFile?: string,
    consultantReportFile?: string,
    link?: string,
    consultant?: string,
    consultantContacts?: string,
    status?: string,
    switcher?: string,
    student?: string,
    course?: number,
    id?: number
}

interface IBiddingData{
    title?: string,
    teacher?: string,
    teacherContacts?: string,
    scienceArea?: string,
    description?: string, 
    reportFile?: string,
    presentationFile?: string,
    consultantReportFile?: string,
    link?: string,
    consultant?: string,
    consultantContacts?: string,
    status?: string,
    switcher?: string,
    student?: string,
    course?: number,
    criticReview?:string,
    id?: number
}


interface ITeacherFreeWork{
    title?: string,
    teacher?: string,
    teacherContacts?: string,
    scienceArea?: string,
    description?: string, 
    status?: string,
    course?: number,
    id?: number,
    studentId?: number
}

interface ITeacherRequest{
    title?: string,
    student?: string,
    course?: number,
    group?: string,
    scienceArea?: string,
    description?: string,
    aboutMe?: string,
    id?: number,
    studentId?: number
}

interface ITeacherCurrentWork{
    title?: string,
    teacher?: string,
    teacherContacts?: string,
    scienceArea?: string,
    description?: string, 
    reportFile?: string,
    presentationFile?: string,
    consultantReportFile?: string,
    link?: string,
    consultant?: string,
    consultantContacts?: string,
    status?: string,
    switcher?: string,
    student?: string,
    course?: number,
    review?: string
    criticReview?:string,
    id?: number
}

interface ITeacherCompletedWork{
    title?: string,
    teacher?: string,
    teacherContacts?: string,
    deadline?: string,
    scienceArea?: string,
    description?: string, 
    reportFile?: string,
    presentationFile?: string,
    consultantReportFile?: string,
    link?: string,
    consultant?: string,
    consultantContacts?: string,
    critic?: string,
    criticReview?: string,
    teacherReview?: string,
    status?: string,
    student?: string,
    course?: number,
    id?: number
}



interface Props{
    page?: string,
    changePage(event : React.MouseEvent<HTMLButtonElement>) : void,
    handleCritic() : void,
    isCritic?: boolean,
    role?: string
}

interface State{

}

class Content extends Component<Props,State>{
    constructor(props : Props){
        super(props);
        this.state ={
        }
    }

    needContentBar(){
        switch(this.props.role){
            case 'student':{  
                if(this.props.page === 'Активные' || this.props.page === 'Завершенные' || this.props.page === 'Мои заявки')
                    return true
                break
            }
            case 'teacher':{
                if(this.props.page === 'Занятые' || this.props.page === 'Свободные' || this.props.page === 'Завершенные' || this.props.page === 'Заявки')
                    return true
                break
            }
            case 'curator':{
                if(this.props.page === 'Занятые темы' || this.props.page === 'Предложенные темы' || this.props.page === 'Заявки')
                    return true
                break
            }
        }
        return false
    }

    needSwitcher(){
        if(this.props.role === 'curator' && (this.props.page === 'Новые рецензенты' || this.props.page === 'Выбранные рецензенты'))
            return true
        return false
    }

    private whichComponent(){
        switch(this.props.role){
            case 'student':{
                if(this.props.page!.indexOf('completed') + 1)
                    return <CourseWorkDetail role={this.props.role} page = {this.props.page}/>

                if(this.props.page!.indexOf('request') + 1)
                    return <RequestDetail page={this.props.page} role={this.props.role}/>


                if(this.props.page!.indexOf('free') + 1)
                    return <FreeWorkDetail page={this.props.page} role={this.props.role}/>


                if(this.props.page!.indexOf('requireCritic') + 1)
                    return <RequireCriticDetail role={this.props.role} page={this.props.page}/>


                if(this.props.page!.indexOf('bidding') + 1)
                    return <BiddingDetail role={this.props.role} page={this.props.page}/>

                break
            }
            case 'teacher':{
                if(this.props.page!.indexOf('st') === 0){
                    return <RequestDetail page={this.props.page} role={this.props.role} />
                }

                if(this.props.page!.indexOf('current') + 1)
                    return <TeachersCurrentWork page={this.props.page}/>


                if(this.props.page!.indexOf('free') + 1){
                    const id = Number(this.props.page!.substr(5))
                    let validRequests : ITeacherRequest[]
                    validRequests = [] // eslint-disable-next-line
                    teacherRequest.map(item =>{
                        if(item.id === id)
                            validRequests.push(item)
                    })
                    let data : ITeacherFreeWork = {} // eslint-disable-next-line
                    teacherMyFreeWorks.map(item => {
                        if(item.id === id) return (data = item)
                    })
                    return <TeacherFreeWorksDetail 
                                page={this.props.page}
                                changePage={this.props.changePage}/>
                }

                if(this.props.page!.indexOf('completed') + 1)
                    return <CourseWorkDetail role={this.props.role} page={this.props.page}/>


                if(this.props.page!.indexOf('foreign') + 1)
                    return <FreeWorkDetail page={this.props.page} role={this.props.role}/>


                if(this.props.page!.indexOf('requireCritic') + 1)
                    return <RequireCriticDetail role={this.props.role} page={this.props.page}/>


                if(this.props.page!.indexOf('bidding') + 1)
                    return <BiddingDetail role={this.props.role} page={this.props.page}/>

                break
            }
            default: return null
        }

    }

    private whichContent(){

        switch(this.props.role){
            case 'student':{
                switch(this.props.page){
                    case 'Главная': 
                        return <Main
                                    role={this.props.role} 
                                    handleCritic={this.props.handleCritic} 
                                    isCritic = {this.props.isCritic} 
                                    changePage={this.props.changePage}/>

                    case 'Активные': 
                        return <CourseWork 
                                    data={activeWork[0]} 
                                    changePage={this.props.changePage} 
                                    role={this.props.role}
                                    type='current'/>

                    case 'Моя курсовая детально': return <MyCourseWorkDetail/>

                    case 'Завершенные': 
                        return <WorksList 
                                    data={completedWorks} 
                                    changePage={this.props.changePage} 
                                    role={this.props.role}
                                    type='completed'/>

                    case 'Мои заявки': 
                        return <RequestsList 
                                    data={requestsData} 
                                    changePage={this.props.changePage} 
                                    role={this.props.role}/>

                    case 'Свободные курсовые': 
                        return <WorksList 
                                    data={freeWorks} 
                                    changePage={this.props.changePage} 
                                    role={this.props.role}
                                    type='free'/>

                    case 'Требуют рецензии': 
                        return <RequireCriticList
                                    data={requireData} 
                                    changePage={this.props.changePage}/>

                    default:
                        return this.whichComponent()
                }
            }
            case 'teacher':{
                switch(this.props.page){
                    case 'Главная': 
                        return <Main
                                    role={this.props.role} 
                                    handleCritic={this.props.handleCritic} 
                                    isCritic = {this.props.isCritic} 
                                    changePage={this.props.changePage}/>

                    case 'Занятые': 
                        return <WorksList 
                                    data={teacherCurrentWorks} 
                                    changePage={this.props.changePage}
                                    role={this.props.role}
                                    type='current'
                                    />

                    case 'Свободные': 
                        return <WorksList
                                    data={teacherMyFreeWorks}
                                    changePage={this.props.changePage}
                                    role={this.props.role}
                                    type='free'/>
                    case 'Завершенные':
                        return <WorksList
                                    data={teacherCompletedWorks}
                                    changePage={this.props.changePage}
                                    role={this.props.role}
                                    type='completed'/>
                    case 'Заявки': 
                        return <RequestsList
                                    data = {teacherRequest}
                                    changePage = {this.props.changePage}
                                    role={this.props.role}/>
                    case 'Свободные курсовые':
                        return <WorksList
                                    data={teacherFreeWorks}
                                    changePage={this.props.changePage}
                                    role={this.props.role}
                                    type='foreign'/>
                    case 'Требуют рецензии':
                        return <RequireCriticList
                                    data={teacherRequireCritic} 
                                    changePage={this.props.changePage}/>
                    default:
                        return this.whichComponent()
                }
            }
            case 'curator':{
                switch(this.props.page){
                    case 'Новые рецензенты':{
                        return <NewCriticList type='not-selected'/>
                    }
                    case 'Выбранные рецензенты':{
                        return <NewCriticList type='selected'/>
                    }
                }
            }
        }
    }

    private renderContent(){
        return(
            <div className='content'>
                {this.needContentBar()? <ContentBar changePage={this.props.changePage} page={this.props.page} role={this.props.role}/> : null}
                {this.needSwitcher()? <CriticSwitcher changePage={this.props.changePage} page={this.props.page}/> : null}
                {this.whichContent()}
            </div>
        )
    }

    render(){
        return this.renderContent();
    }
}

export default Content
