import React, {Component} from "react";
import ServiceTopBar from "./Components/TopBar/ServiceTopBar";
import Menu from './Components/Menu/Menu'
import MainWindow from './Components/MainWindow/MainWindow'
import Footer from './Components/Footer/Footer'

interface Props{

}

interface State{
    page?: string
}

class App extends Component<Props,State> {
    constructor(props : Props){
        super(props);
        this.state = {
            page : 'Главная'
        }
    }

    changePage = (event : React.MouseEvent<HTMLButtonElement>) => {
        const newPage = event.currentTarget.value
        if(newPage === 'Мои курсовые')
            return(this.setState({page:'Активные'}))
        return(this.setState({page:newPage}))
    }


    render() {
        return (
        <div className='page'>
            <div style={{position :'fixed', width : '100vw', margin : '0', zIndex: '99999'}}><ServiceTopBar/></div>
            <Menu page={this.state.page} changePage={this.changePage}/>
            <MainWindow page={this.state.page} changePage={this.changePage}/>
            <Footer/>
        </div>
        )}
}

export default App;
