import React, { Component } from 'react'
import Notifications from './Components/Notifications/Notifications'
import NewWorks from './Components/NewWorks/NewWorks'
import { Typography } from '@material-ui/core';
import SidePage from '@skbkontur/react-ui/SidePage'
import Button from '@skbkontur/react-ui/Button'
import './Main.css'

interface Props{
    isCritic?: boolean,
    handleCritic() : void,
    changePage(event : React.MouseEvent<HTMLButtonElement>): void,
    role?: string
}

interface State{
    isCritic?: boolean,
    opened?: boolean
}

class Main extends Component<Props,State>{
    constructor(props : Props){
        super(props);
        this.state={
            isCritic : this.props.isCritic,
            opened : false
        }
    }

    private changeCriticStatus = () => {
        let newCriticStatus = !this.state.isCritic
        this.setState({isCritic : newCriticStatus})
        this.props.handleCritic()
    }

    private renderSidePage() {
        return (
          <SidePage onClose={this.close} blockBackground>
            <SidePage.Header>Title</SidePage.Header>
            <SidePage.Body>
              <div
                style={{
                  background: `repeating-linear-gradient(
                                60deg,
                                #fafafa,
                                #fafafa 20px,
                                #dfdede 20px,
                                #dfdede 40px
                              )`,
                  height: 600,
                  padding: '20px 0',
                }}
              >
                <SidePage.Container>
                  <p>Use rxjs operators with react hooks</p>
                </SidePage.Container>
              </div>
            </SidePage.Body>
            <SidePage.Footer panel>
              <Button onClick={this.close}>Close</Button>
            </SidePage.Footer>
          </SidePage>
        );
      }

    private open = () => {
    this.setState({ opened: true });
    }

    private close = () => {
    this.setState({ opened: false });
    }

    private renderMain(){
        return(
            <div>
                <Notifications isCritic={this.props.isCritic} changePage={this.props.changePage}/>
                <NewWorks/>
                {this.props.role === 'teacher'?
                    <button
                        className='mainButton'
                        onClick={this.open}
                    ><Typography variant='button'>Предложить тему курсовой работы</Typography></button>    
                :null}

                <div>
                {this.state.opened && this.renderSidePage()}
                <Button onClick={this.open}>Open</Button>
                </div>;

                {!this.state.isCritic?
                    <button
                        className='mainButton critic'
                        onClick = {this.changeCriticStatus}
                    ><Typography variant='button'>Стать рецензентом</Typography></button>
                : <button
                    className='criticButton noCritic'
                    onClick = {this.changeCriticStatus}
                ><Typography variant='button'>Перестать быть рецензентом</Typography></button>}
            </div>
        )
    }

    render(){
        return this.renderMain();
    }
}

export default Main
