import React from 'react'
import Typography from '@material-ui/core/Typography'
import Gapped from '@skbkontur/react-ui/Gapped'

import biddingList from '../../../../../TestData/Curator/biddingList'

interface Idata{
    title?: string,
    id?: number,
    critic?: string,
    department?: string
}

interface Props{

}

interface State{
    data : Idata[]
}

class BiddingList extends React.Component<Props,State>{
    constructor(props : Props){
        super(props)
        this.state = {
            data : [{}]
        }
    }

    componentDidMount(){
        this.setState({data : biddingList})
    }

    private renderItem(item : Idata){
        return(
            <div>
                <Gapped>
                    <Typography>{item.title} --</Typography>
                    <Typography>{item.critic}</Typography>
                    <button value={'bidding_' + item.id}>Kek</button>
                </Gapped>
            </div>
        )
    }

    private renderList(){
        return(
            this.state.data.map(item => this.renderItem(item))
        )
    }

    private isEmpty(obj : any) {
        return Object.keys(obj).length === 0;
    }

    private renderEmptyList(){
        return(
            <div style={{textAlign:"center"}}>
                <Typography variant='h5'>Нет результатов биддинга</Typography>
            </div>
        )
    }

    render(){
        return(
            !this.isEmpty(this.state.data[0])?
                this.renderList()
            : this.renderEmptyList()
        )
    }
}

export default BiddingList
