import * as React from "react";
import jwt_decode from "jwt-decode";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import Modal from '@skbkontur/react-ui/Modal'

interface ILoginState {
    email: string;
    password: string;
    logged: boolean;
    needMoreInformation: boolean,
    user : IUser,
    token : string
}

interface IUser{
    firstName?: string,
    lastName?: string,
    isCritic?: boolean,
    role?: string,
    userId?: number
}

interface Props{
    auth (user : IUser, token : string) : void
}

export default class Login extends React.Component<Props, ILoginState> {
    constructor(props: Props) {
        super(props);
        this.state = {
            email: "",
            password: "",
            logged: false,
            needMoreInformation : false,
            user : {},
            token : ''
        };
    }

    private renderModal() {
        return (
          <Modal onClose={this.closeModal}>
            <Modal.Header>Заполните необходимую информацию</Modal.Header>
            <Modal.Body>
                <Typography variant='h6'>Здесь будет ввод необходимой инфы</Typography>
            </Modal.Body>
            <Modal.Footer>
                <Button onClick={e => {this.setState({needMoreInformation : false})}}>Отправить</Button>
                <Button onClick={this.closeModal}>Закрыть</Button>
            </Modal.Footer>
          </Modal>
        );
    }

    private afterModalAuth = () => {
        this.props.auth(this.state.user, this.state.token)
    }

    private closeModal = () => {
        this.setState({needMoreInformation : false})
    }

    private openModal = () => {
        this.setState({needMoreInformation : true})
    }


    render(): JSX.Element {
        return (
            <div className="container">
                <Typography variant="h6" gutterBottom>Войти</Typography>
                {this.state.needMoreInformation && this.renderModal()}
                <div>
                    <TextField
                        required
                        type="email"
                        label="Email"
                        variant="outlined"
                        margin="normal"
                        name={this.state.email}
                        onChange={e => this.setState({ email: e.target.value })}
                    />
                </div>
                <TextField
                    required
                    type="password"
                    label="Password"
                    variant="outlined"
                    margin="normal"
                    value={this.state.password}
                    onChange={e => this.setState({ password: e.target.value})}
                />
                <br />
                <Button size="small" variant="contained" color="primary" onClick={this.handleSubmit}>Войти</Button>
            </div>
        );
    }

    private handleSubmit = async () => {
        const {email, password} = this.state;
        //-----------------------------
        // login-запрос, получаю токен
        //-----------------------------
        let token : string ='eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJzdWIiOiIxMjM0NTY3ODkwIiwibmFtZSI6IkpvaG4gRG9lIiwiaWF0IjoxNTE2MjM5MDIyfQ.SflKxwRJSMeKKF2QT4fwpMeJf36POk6yJV_adQssw5c'
        this.setState({token : token})
        //let user : {} = jwt_decode(token);
        this.setState({
            user:{
                firstName : 'Aleksandr',
                lastName : '',
                isCritic : false,
                role: 'student',
                userId : 1
            }
        })

        //если какой-то информации не хватает
        if(this.state.user.lastName === ''){
            this.openModal()
        }
        else
            this.props.auth(this.state.user, this.state.token)
    }
}
