import React, { Component } from 'react'
import Typography from '@material-ui/core/Typography'
import './RequireCriticList.css'
import Switcher from '@skbkontur/react-ui/Switcher'
import Gapped from '@skbkontur/react-ui/Gapped'

interface Idata{
    title?: string,
    teacher?: string,
    teacherContacts?: string,
    scienceArea?: string,
    description?: string, 
    reportFile?: string,
    presentationFile?: string,
    consultantReportFile?: string,
    link?: string,
    consultant?: string,
    consultantContacts?: string,
    status?: string,
    switcher?: string,
    id?: string
}

interface Props{
    data?: {}[]
}

interface State{
    data?: Idata[]
}

class RequireCriticList extends Component<Props,State>{
    constructor(props : Props){
        super(props);
        this.state={
            data : this.props.data
        }
    }

    private handleSwtcher = (event : {target : {value:string}, value:string}) => {

    }

    private renderItem(item : Idata){
        return(
            <div className='requireItem'>
                <div className='requireTitle'><Typography variant='h6'>{item.title}, {item.teacher}</Typography></div>
                <Gapped gap={20}>
                    <button
                        className='more'
                    ><Typography variant='button'>Подробнее</Typography></button>
                    <Switcher
                        items={['Да','Мб','Нет']}
                        value={item.switcher}
                        onChange={this.handleSwtcher}
                        />
                </Gapped>
            </div>
        )
    }

    private renderRequireCriticList(){
        return(
            <div>
                {this.props.data?.map(item => this.renderItem(item))}
                <hr/>
            </div>
        )
    }

    render(){
        return this.renderRequireCriticList();
    }
}

export default RequireCriticList
