import React, { Component } from 'react'
import './Content.css'
import ContentBar from './ContentBar/ContentBar'
import MyCourseWork from './MyCourseWork/MyCourseWork'
import MyCourseWorkDetail from './MyCourseWorkDetail/MyCourseWorkDetail'

interface Props{
    page?: string
}

interface State{
    section?: string
}

class Content extends Component<Props,State>{
    constructor(props : Props){
        super(props);
        this.state ={
            section:'Активные'
        }
    }

    changeSection = (event : React.MouseEvent<HTMLButtonElement>) => {
        const newSection = event.currentTarget.value
        return(this.setState({section:newSection}))
    }

    private renderContent(){
        return(
            <div className='content'>
                {(this.props.page === 'Мои курсовые' && (this.state.section === 'Активные' || 'Завершенные' || 'Мои заявки'))? <ContentBar changeSection={this.changeSection}/> : null}
                {(this.props.page === 'Мои курсовые' && this.state.section === 'Активные')? <MyCourseWork changeSection={this.changeSection}/> : null}
                {(this.props.page === 'Мои курсовые' && this.state.section === 'Моя курсовая')? <MyCourseWorkDetail/> : null}
            </div>
        )
    }

    render(){
        return this.renderContent();
    }
}

export default Content
