import React, { Component } from 'react'
import './Menu.css'
import student from './MenuItems/StudentMenuItems'
import teacher from './MenuItems/TeacheMenuItems'
import critic from './MenuItems/CriticMenuItems'
import curator from './MenuItems/CuratorMenuItems'


interface Props {

}

interface State {
    items: {title: string, disabled: boolean }[],
    items1: {title: string, disabled: boolean }[],
    items2: {title: string, disabled: boolean }[],
    items3: {title: string, disabled: boolean }[]
}

class Menu extends Component<Props, State>{
    constructor(props: Props) {
        super(props);
        this.state = {
            items: student,
            items1:teacher,
            items2:critic,
            items3:curator
        }
    }

    isSelected(item: boolean) {
        return item ? "selected" : ""
    }

    handleChange = (event : React.MouseEvent<HTMLDivElement>) => {
        let arr = this.state.items.map(item => {
            if(item.title === event.currentTarget.textContent) item.disabled = true
            else item.disabled = false
            return item
        })
        this.setState({items:arr})

    }

    private main() {
        return (
            <div className='menu'>
                {this.state.items.map(item => {
                    return (
                        <div 
                            className={'menuItem ' + this.isSelected(item.disabled)}
                            onClick={this.handleChange}

                        >{item.title}
                        </div>
                    )
                })}
                <hr/>
                {this.state.items1.map(item => {
                    return (
                        <div 
                            className={'menuItem ' + this.isSelected(item.disabled)}
                            onClick={this.handleChange}

                        >{item.title}
                        </div>
                    )
                })}
                <hr/>
                {this.state.items2.map(item => {
                    return (
                        <div 
                            className={'menuItem ' + this.isSelected(item.disabled)}
                            onClick={this.handleChange}

                        >{item.title}
                        </div>
                    )
                })}
                <hr/>
                {this.state.items3.map(item => {
                    return (
                        <div 
                            className={'menuItem ' + this.isSelected(item.disabled)}
                            onClick={this.handleChange}

                        >{item.title}
                        </div>
                    )
                })}
            </div>
        )
    };

    render() {
        return this.main();
    }
}

export default Menu
