import React, { Component } from 'react'
import SidePage from '@skbkontur/react-ui/SidePage'
import Button from '@skbkontur/react-ui/Button'


interface Props{
    openSidePage() : void,
    closeSidePage() : void
}

interface State{

}

class ContentBar extends Component<Props,State>{
    constructor(props : Props){
        super(props);
        this.state={}
    }

    private renderSidePage() {
        return (
          <SidePage onClose={this.props.closeSidePage} blockBackground>
            <SidePage.Header>Title</SidePage.Header>
            <SidePage.Body>
              <div
                style={{
                  background: `repeating-linear-gradient(
                                60deg,
                                #fafafa,
                                #fafafa 20px,
                                #dfdede 20px,
                                #dfdede 40px
                              )`,
                  height: 600,
                  padding: '20px 0',
                }}
              >
                <SidePage.Container>
                  <p>Use rxjs operators with react hooks</p>
                </SidePage.Container>
              </div>
            </SidePage.Body>
            <SidePage.Footer panel>
              <Button onClick={this.props.closeSidePage}>Close</Button>
            </SidePage.Footer>
          </SidePage>
        );
      }


    render(){
        return this.renderSidePage();
    }
}

export default SidePage
