import React, {Component} from 'react'
import Switcher from '@skbkontur/react-ui/Switcher'



interface Props{

}

interface State{
    switchState: string
}

class CriticSwitcher extends Component<Props,State>{
    constructor(props : Props){
        super(props);
        this.state = {
            switchState : 'Новые рецензенты'
        }
    }

    render(){
        return(
            <div className='ml20'>
                <Switcher
                    items={['Новые рецензенты', 'Выбранные рецензенты']}
                    value={this.state.switchState}
                    onChange={value => this.setState({switchState : value.target.value})}
                    />
            </div>

        )
    }

}

export default CriticSwitcher
