import React, { Component } from 'react'
import './Content.css'
import ContentBar from './ContentBar/ContentBar'
import CourseWork from './MyCourseWork/MyCourseWork'

interface Props{
    page?: string
}

interface State{
    section?: string,
    contentPage?: string
}

class Content extends Component<Props,State>{
    constructor(props : Props){
        super(props);
        this.state ={
            section:'Активные',
            contentPage:'Активные'
        }
    }

    changeSection = (newSection?: string) => {
        return(this.setState({section : newSection!}))
    }

    changeContentPage= (newContendPage?: string) => {
        return(this.setState({contentPage:newContendPage!}))
    }

    private renderContent(){
        return(
            <div className='content'>
                {this.props.page==='Мои курсовые'? <ContentBar changeSection={this.changeSection}/> : null}
                {(this.props.page==='Мои курсовые' && this.state.section === 'Активные')? <CourseWork/> : null}
            </div>
        )
    }

    render(){
        return this.renderContent();
    }
}

export default Content
