import React, {Component} from "react";
import ServiceTopBar from "./Components/TopBar/ServiceTopBar";
import Menu from './Components/Menu/Menu'
import MainWindow from './Components/MainWindow/MainWindow'

interface Props{

}

interface State{
    page?: string,
    section?: string
}

class App extends Component<Props,State> {
    constructor(props : Props){
        super(props);
        this.state = {
            page : 'Главная'
        }
    }

    changePage = (newPage? :string) => {
        if(newPage === 'Мои курсовые')
            return(this.setState({page:newPage,section:'Активные'}))
        else return(this.setState({page:newPage}))
    }

    changeSection = (event : React.MouseEvent<HTMLButtonElement>) => {
        const newSection = event.currentTarget.value
        return (this.setState({section : newSection}))
    }

    render() {
        return (
        <div className='page'>
            <ServiceTopBar/>
            <Menu page={this.state.page} changePage={this.changePage}/>
            <MainWindow page={this.state.page} section={this.state.section}/>
        </div>
        )}
}

export default App;
