import React, {Component} from "react";
import ServiceTopBar from "./Components/TopBar/ServiceTopBar";
import Menu from './Components/Menu/Menu'
import MainWindow from './Components/MainWindow/MainWindow'
import Footer from './Components/Footer/Footer'

import userData from './TestData/userData'

interface Props{

}

interface IUser{
    firstName?: string,
    lastName?: string,
    isCritic?: boolean
}

interface State{
    page?: string,
    user : IUser
}


class App extends Component<Props,State> {
    constructor(props : Props){
        super(props);
        this.state = {
            page : 'Главная',
            user : userData
        }
    }

    changePage = (event : React.MouseEvent<HTMLButtonElement>) => {
        const newPage = event.currentTarget.value
        if(newPage === 'Мои курсовые')
            return(this.setState({page:'Активные'}))
        return(this.setState({page:newPage}))
    }

    handleCritic = () => {
        let newUserData =  this.state.user
        newUserData.isCritic = !newUserData.isCritic
        this.setState({user : newUserData})
    }

    render() {
        return (
        <div className='page'>
                <div className='topBar'><ServiceTopBar/></div>
                <div className='mainContent'>
                    <Menu page={this.state.page} changePage={this.changePage} isCritic={this.state.user.isCritic}/>
                    <MainWindow page={this.state.page} changePage={this.changePage} handleCritic = {this.handleCritic}/>
                </div>
                <Footer/>
        </div>
        )}
}

export default App;
