import React from 'react'
import Link from '@skbkontur/react-ui/Link'
import Toast from '@skbkontur/react-ui/Toast'

interface Props{
    data : {
        title?: string,
        teacher?: string,
        deadline?: string,
        scienceArea?: string,
        description?: string,
        reportFile?: string,
        presentationFile?: string,
        consultantReportFile?: string,
        link?: string,
    }
}

class AttachedFiles extends React.Component<Props>{

    private downloadFile = () => {
        Toast.push('Скачивание...')
    }

    render(){
        return(
        <div className='attached'>
            {this.props.data.reportFile !== ''? <div><span>Отчет: </span><Link use='grayed'onClick={this.downloadFile}>{this.props.data.reportFile}</Link></div> : null }
            <Link use='grayed'onClick={this.downloadFile}>{this.props.data.presentationFile !== ''? 'Презентация: ' + this.props.data.presentationFile : ''}</Link>
            <Link use='grayed'onClick={this.downloadFile}>{this.props.data.consultantReportFile !== ''? 'Отзыв консультанта: ' + this.props.data.consultantReportFile : ''}</Link>
            <Link use='grayed'onClick={this.downloadFile}>{this.props.data.link !== ''? 'Ссылка: ' + this.props.data.link : ''}</Link>
        </div>
        )
    }
}

export default AttachedFiles
